import React,{Component} from "react";
import ToDoService from "../../api/todo/ToDoDataService.js";
import AuthenticationService from "./AuthenticationService.js"

class ToDosComponent extends Component {
    constructor(props){
        super(props)
            this.state = {
                todos : []
                // [
                // {id:1, description:'Learn React', done:false, targetDate: new Date()},
                // {id:2, description:'Learn React', done:false, targetDate: new Date()},
                // {id:3, description:'Learn React', done:false, targetDate: new Date()}
                // ]
            }
        }
        componentDidMount(){
            const username = AuthenticationService.getUserLoggedIn();
            ToDoService.retrieveAllToDos(username)
            .then(response => {
                console.log(response)
            })
        }
    
    render(){
        return(
            <div>
                <h1>List ToDos</h1>
                <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Is completed?</th>
                            <th>Target Date</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map( 
                                todo => 
                                <tr key={todo.id}>
                                    <td>{todo.description}</td>
                                    <td>{todo.done.toString()}</td>
                                    <td>{todo.targetDate.toString()}</td>
                                </tr>
                            )
                            }
                    </tbody>
                </table>
                </div>
            </div>
        )
    }
}

export default ToDosComponent;
