import React,{Component} from "react";
import ToDoService from "../../api/todo/ToDoDataService.js";
import AuthenticationService from "./AuthenticationService.js"

class ToDosComponent extends Component {
    constructor(props){
        super(props)
            this.state = {
                todos : []
            }
            this.deleteToDo = this.deleteToDo.bind(this);
        }
        componentDidMount(){
            const username = AuthenticationService.getUserLoggedIn();
            ToDoService.retrieveAllToDos(username)
            .then(
                response => {
                this.setState({todos : response.data})
            })
        }
    
    render(){
        return(
            <div>
                <h1>List ToDos</h1>
                <div className="container">
                <table className="table">
                    <thead>
                        <tr>
                            <th>Description</th>
                            <th>Is completed?</th>
                            <th>Target Date</th>
                            <th>Delete</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.todos.map( 
                                todo => 
                                <tr key={todo.id}>
                                    <td>{todo.description}</td>
                                    <td>{todo.completed.toString()}</td>
                                    <td>{todo.targetDate.toString()}</td>
                                    <td><button className="btn btn-warning" onClick={this.deleteToDo}>Delete</button></td> 
                                </tr>
                            )
                            }
                    </tbody>
                </table>
                </div>
            </div>
        )
    }
    deleteToDo(){
        console.log("Delete Button Presses");
    }
}

export default ToDosComponent;
